import React from 'react';
import { connect } from 'react-redux';
import * as konva from 'react-konva';

const waterB64 = 'data:image/jpeg;base64,';
const bgImg = new Image();
bgImg.src = waterB64;

const Background = ({ width, height }) => (
  <konva.Layer>
    <konva.Image
      x="0"
      y="0"
      width={width}
      height={height}
      fillPatternImage={bgImg}
      fillPatternRepeat="repeat"
    />
  </konva.Layer>
);

export default connect(({ app: { width, height } }) => ({
  width, height
}))(Background);
